import React from "react";
import ReactDOM from "react-dom";
import queryString from "query-string";
import { ThemeProvider } from "styled-components";
import theme from "./theme";
import "./styles.css";

import {
  Sidebar,
  Section,
  Heading,
  Description,
  Option,
  Input,
  TextArea,
  Button,
  Switch,
  Preview
} from "./components";

import Editor, { DiffEditor, useMonaco, loader } from "@monaco-editor/react";

// outside of presets
const globalOptions = {
  fontSize: 14,
  highlightLines: null,
  initialPath: "/",
  useCodemirror: false,
  enableEslint: false,
  defaultFile: null,
  showNavigation: false
};

const presets = {
  "split-view": {
    showEditor: true,
    showTabbarSidebar: true,
    showPreview: true,
    showNavigation: false,
    expandDevtools: false,
    showTests: false
  },
  "preview-only": {
    showEditor: false,
    showPreview: true,
    showNavigation: false,
    expandDevtools: false,
    showTests: false
  },
  "code-only": {
    showEditor: true,
    showTabbarSidebar: false,
    showPreview: false
  },
  "code-with-tests": {
    showEditor: true,
    showTabbarSidebar: true,
    showPreview: true,
    showNavigation: false,
    expandDevtools: true,
    showTests: true
  }
};

function App() {
  const [settings, setSettings] = React.useState({
    preset: "split-view",
    ...globalOptions,
    ...presets["split-view"]
  });

  const [darkMode, setDarkMode] = React.useState(true);

  const change = property => {
    setSettings({ ...settings, preset: "custom", ...property });
  };

  const toggle = property => {
    change({ [property]: !settings[property] });
  };

  const applyPreset = name => {
    setSettings({ ...settings, preset: name, ...presets[name] });
  };

  const select = event => {
    event.target.select()
  }

  return (
    <ThemeProvider theme={theme}>
      <div className="modal">
        <Sidebar>
          <Section.Body css={{ paddingBottom: 0 }}>
            <Heading>Template View</Heading>
            <Description>
              Dont complicate shit pls
            </Description>

            <Option>
              Dark theme
              <Switch on={darkMode} onChange={() => setDarkMode(!darkMode)} />
            </Option>
          </Section.Body>

          <Section title="Payload">
            <Option>
              Show Editor
              <Switch
                on={settings.showEditor}
                onChange={() => toggle("showEditor")}
              />
            </Option>
            <Option disabled={!settings.showEditor}>
              <span style={{ textDecoration: "line-through" }}>
                Tabbar + Sidebar
              </span>
              <Switch
                on={settings.showEditorControls}
                disabled={!settings.showEditor}
                onChange={() => toggle("showEditorControls")}
              />
            </Option>
            <Option disabled={!settings.showEditor}>
              Font-size
              <Input
                type="number"
                defaultValue={settings.fontSize}
                disabled={!settings.showEditor}
                onChange={event => change({ fontSize: event.target.value })}
              />
            </Option>
            <Option disabled={!settings.showEditor}>
              Highlight lines (with CodeMirror)
              <Input
                type="text"
                defaultValue={settings.highlightLines}
                onChange={event =>
                  change({ highlightLines: event.target.value })
                }
                disabled={!settings.showEditor}
              />
            </Option>
          </Section>

          <Section title="Metadata">
            <Option>
              Show Preview
              <Switch
                on={settings.showPreview}
                onChange={() => toggle("showPreview")}
              />
            </Option>
            <Option disabled={!settings.showPreview}>
              Show Navigation Bar
              <Switch
                on={settings.showNavigation}
                onChange={() => toggle("showNavigation")}
                disabled={!settings.showPreview}
              />
            </Option>
            <Option disabled={!settings.showPreview}>
              Expand Dev Tools
              <Switch
                disabled={!settings.showPreview}
                onChange={() => toggle("expandDevTools")}
              />
            </Option>
            <Option disabled={!settings.showPreview}>
              Show Tests (instead of preview)
              <Switch
                on={settings.showTests}
                onChange={() => toggle("showTests")}
                disabled={!settings.showPreview}
              />
            </Option>
          </Section>
          <Section title="Locale">
            <Option>
              Use CodeMirror insted of Monaco
              <Switch on />
            </Option>
            <Option>
              Enable eslint (bigger bundle size)
              <Switch />
            </Option>
            <Option multiline>
              Project Initial Path
              <Input placeholder="e.g. /home" />
            </Option>
          </Section>
        </Sidebar>
        <Preview>
          <Editor
            height="100vh"
            defaultLanguage="html"
            defaultValue="// some comment"
            theme="vs-dark"
          />
        </Preview>
      </div>
    </ThemeProvider>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
